import React, { useLayoutEffect, useRef } from 'react';
import type { Message } from '../types';

type MessageThreadProps = {
  message: Message;
  messages: Message[];
  childIndexes: { [parentId: string]: number };
  setChildIndexes: React.Dispatch<React.SetStateAction<{ [parentId: string]: number }>>;
  onRender: (id: string) => void;        // новый пропс
};

export const MessageThread: React.FC<MessageThreadProps> = ({
  message,
  messages,
  childIndexes,
  setChildIndexes,
  onRender,
}) => {
  const children = messages.filter((m) => m.parent_id === message.id);
  const index = childIndexes[message.id] || 0;
  const selfRef = useRef<HTMLDivElement>(null);

  // Вызвать onRender сразу после того, как div с сообщением будет помещён в DOM
  useLayoutEffect(() => {
    if (selfRef.current) {
      onRender(message.id);
    }
  }, [message.id, onRender]);

  const handleNext = () => {
    setChildIndexes((prev) => ({
      ...prev,
      [message.id]: (index + 1) % children.length,
    }));
  };

  const handlePrev = () => {
    setChildIndexes((prev) => ({
      ...prev,
      [message.id]: (index - 1 + children.length) % children.length,
    }));
  };

  return (
    <>
      {/* Родительское сообщение */}
      {message.parent_id === "" && (
        <div className={`message ${message.role}`} ref={selfRef}>
          {message.content}
        </div>
      )}

      {/* Дочерние */}
      {children.length > 0 && (
        <div>
          <div className={`message ${message.role}`} ref={selfRef}>
            <div className="message-content">
              {children[index].content}
            </div>
            {children.length > 1 && (
              <div className="message-controls">
                <button onClick={handlePrev}>◀️</button>
                <button onClick={handleNext}>▶️</button>
              </div>
            )}
          </div>

          {/* Рекурсия */}
          <MessageThread
            message={children[index]}
            messages={messages}
            childIndexes={childIndexes}
            setChildIndexes={setChildIndexes}
            onRender={onRender}
          />
        </div>
      )}
    </>
  );
};
