// src/hooks/useMessages.ts
import { useState, useEffect, useCallback } from 'react';
import {
  getMessages,
  createMessage,
  getMessage,
} from '../api/messages';
import { MessageIn, MessageOut } from '../api/types';
import { Message } from '../types';
import { transformMessageOut, transformMessages } from '../utils/transform';

export function useMessages(chatId: string | null) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);


  useEffect(() => {
    fetchMessages()
  }, [chatId]);

  const fetchMessages = async () => {
      try {
        if (!chatId) { return; }
        const res = await getMessages(chatId);
        const trans = transformMessages(res)
        setMessages(trans);
        console.log(messages)

      } catch (err) {
        console.error('Ошибка при получении чатов:', err);
      } finally {
        setLoading(false);
      }
    };

  const send = useCallback(
    async (msg: MessageIn) => {
      const newID = await createMessage(msg);
      const newMsg = await getMessage(newID)
      setMessages(prev => [...prev, transformMessageOut(newMsg)]);
      return transformMessageOut(newMsg);
    },
    []
  );

  return { messages, setMessages, loading, error, send, fetchMessages};
}

export function useStreamMessage(chatId: string | null) {
  const {send} = useMessages(chatId)
  const [chunks, setChunks] = useState<string[]>([]);
  const [error, setError] = useState<string | null>(null);

  const start = useCallback((id: string, chat_id: string, newMessage: Message) => {
    setChunks([]); // обнуляем при новом запуске
    const evtSource = new EventSource(`https://giicoo.ru/api/message-service/stream?id=${encodeURIComponent(id)}`);

    evtSource.onmessage = e => {
      setChunks(prev => [...prev, e.data]);
    };

    evtSource.onerror = e => {
      setError('Stream error');
      evtSource.close();
    };

    return () => {
      evtSource.close();
      const req: MessageIn = {
        chat_id: chatId,

      }
      send(req)
    };
  }, []);

  return { chunks, error, start };
}
