import api from './client';
import { MessageIn, MessageOut } from './types';

// Создать сообщение
export async function createMessage(payload: MessageIn): Promise<MessageOut> {
  const res = await api.post('/message', payload);
  return res.data;
}

// Получить все сообщения чата
export async function getMessages(chatId: string): Promise<MessageOut[]> {
  const res = await api.get('/messages', { params: { chat_id: chatId } });
  return res.data;
}
