import React from 'react';
import type { Message } from '../types';

type MessageThreadProps = {
  message: Message;
  messages: Message[];
  childIndexes: { [parentId: string]: number };
  setChildIndexes: React.Dispatch<React.SetStateAction<{ [parentId: string]: number }>>;
  setReplyContext: React.Dispatch<React.SetStateAction<{ messageId: string; messageText: string }>>;
};

export const MessageThread: React.FC<MessageThreadProps> = ({
  message,
  messages,
  childIndexes,
  setChildIndexes,
}) => {
  const children = messages.filter((m) => m.parent_id === message.id);
  const index = childIndexes[message.id] || 0;

  const handleNext = () => {
    setChildIndexes((prev) => ({
      ...prev,
      [message.id]: (index + 1) % children.length,
    }));
  };

  const handlePrev = () => {
    setChildIndexes((prev) => ({
      ...prev,
      [message.id]: (index - 1 + children.length) % children.length,
    }));
  };

  return (
    <>
    {/* –†–æ–¥–∏—Ç–µ–ª—å—Å–∫–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ */}
    {message.parent_id === "" && (
      <div
        className={`message ${message.sender} ${message.replyTo ? 'has-reply' : ''}`}
        onClick={() => setReplyContext({ messageId: message.id, messageText: message.content })}
      >
        {/* –ö–æ–Ω—Ç–µ–∫—Å—Ç –æ—Ç–≤–µ—Ç–∞ (–µ—Å–ª–∏ —Å–æ–æ–±—â–µ–Ω–∏–µ —è–≤–ª—è–µ—Ç—Å—è –æ—Ç–≤–µ—Ç–æ–º –Ω–∞ –¥—Ä—É–≥–æ–µ) */}
        {message.replyTo && (
          <div className="reply-context">
            <span className="reply-to-label">–û—Ç–≤–µ—Ç –Ω–∞:</span>
            {messages.find(m => m.id === message.replyTo)?.content}
          </div>
        )}

        {/* –ü—Ä–∏–∫—Ä–µ–ø–ª–µ–Ω–∏—è (–Ω–∞–ø—Ä–∏–º–µ—Ä, –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –∏–ª–∏ –¥–æ–∫—É–º–µ–Ω—Ç—ã) */}
        {message.attachment && (
          <div className="attachment">
            {message.attachment.type === 'image' ? (
              <img src={message.attachment.url} alt="–ü—Ä–∏–∫—Ä–µ–ø–ª–µ–Ω–Ω–æ–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ" />
            ) : (
              <a href={message.attachment.url} download>
                üìÑ {message.attachment.name || '–î–æ–∫—É–º–µ–Ω—Ç'}
              </a>
            )}
          </div>
        )}

        {/* –¢–µ–∫—Å—Ç —Å–æ–æ–±—â–µ–Ω–∏—è */}
        <div className="message-text">{message.content}</div>

        {/* –í—Ä–µ–º—è –æ—Ç–ø—Ä–∞–≤–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏—è */}
        <div className="message-time">
          {new Date(message.created_at || '').toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </div>
      </div>
    )}

    {/* –ï—Å–ª–∏ –µ—Å—Ç—å –¥–µ—Ç–∏ ‚Äî —Ä–µ–Ω–¥–µ—Ä–∏–º –∏—Ö –∫–∞–∫ –æ—Ç–¥–µ–ª—å–Ω—ã–µ –±–ª–æ–∫–∏ */}
    {children.length > 0 && (
      <div className="child-message-wrapper">
        <div className="message child">
          <div className="message-content">
            {children[index].content}
          </div>
          <div className="message-controls">
            <button onClick={handlePrev}>‚óÄÔ∏è</button>
            <button onClick={handleNext}>‚ñ∂Ô∏è</button>
          </div>
        </div>

        {/* –†–µ–∫—É—Ä—Å–∏–≤–Ω—ã–π —Ä–µ–Ω–¥–µ—Ä –¥–ª—è –¥–µ—Ç–µ–π */}
        <MessageThread
          message={children[index]}
          messages={messages}
          childIndexes={childIndexes}
          setChildIndexes={setChildIndexes}
          setReplyContext={setReplyContext}
        />
      </div>
    )}
  </>
  );
};
