// src/api/chats.ts
import api from './client';
import { ChatIn, ChatOut } from './types';

export async function createChat(payload: ChatIn) {
  const res = await api.post('/create', payload);
  return res.data;
}

export async function createChatAuto(query: string, payload: ChatIn) {
  const res = await api.post(`/create-auto?query=${encodeURIComponent(query)}`, payload);
  return res.data;
}

export async function getChat(chatId: string): Promise<ChatOut> {
  const res = await api.get(`/get/${chatId}`);
  return res.data;
}

export async function getAllChats(telegramId: number): Promise<ChatOut[]> {
  const res = await api.get(`/get-all/${telegramId}`);
  return res.data;
}

export async function deleteChat(chatId?: string) {
  const url = chatId ? `/delete?chat_id=${encodeURIComponent(chatId)}` : '/delete';
  const res = await api.delete(url);
  return res.data;
}
